package Section5.Exercise24;public class FlourPackProblem {    public static void main(String[] args) {        System.out.println(canPack(0,5,4));        System.out.println(canPack(3,15,4));        System.out.println(canPack(0,0,4));        System.out.println(canPack(-5,5,5));    }    public static boolean canPack(int bigCount, int smallCount, int goal){        if ((bigCount<0) || (smallCount<0) || (goal<0)) {            return false;        } else {            for (int i=0; i<=bigCount; i++){                for (int j=0; j<=smallCount; j++){                    if (((i*5) + j)==goal) {                        return true;                    }                }            }        } return false;    }}